            })
            
        } else {
            console.log("xato");
            this.setState({

                boxRight: [
                    {
                        value: b,
                        bool: a
                    }
                ]
            })
        }
        
    }

    render() {
        return (
            <div className="Box">
                <div className="Left">
                    {
                        this.state.boxLeft.map((item, index) => <>
                        <input type="submit" value={item.value} onClick={this.btn} onChange={this.Full} />
                        </>)
                    }
                </div>
                <div className="Right">
                    {
                        this.state.boxRight.map((item, index) => <>
                        <input type="submit" value={item.value} onClick={item.bool} onChange={this.Fill} />
                        </>)
                    }
                </div>

            </div>
        )
    }
}
