import React, { Component } from "react";

export default class Child extends Component {
  state = {
    hour: 0,
    minute: 0,
    secound: 0,
    stopIntervalTimer: '',
    interval: []
  };

  componentWillMount() {

    alert()

    startTimer = () => {
      let started = setInterval(() => {
        const { hour, minute, secound } = this.state;

        if (secound === 59) {
          if (minute === 59) {
            this.setState({
              hour: hour + 1,
              minute: 0,
            });
          } else {
            this.setState({
              minute: minute + 1,
              secound: 0,
            });
          }
        } else {
          this.setState({
            secound: secound + 1,
          });
        }

        this.setState({
            stopIntervalTimer: started
        })


      }, 10);
    };
  }

  stopTimer = () => {
      clearInterval(this.state.stopIntervalTimer)
  }



  intervalGet = () => {
      const {hour, minute, secound} = this.state

      let array = this.state.interval

      array.push(hour + " : " + minute + " : " + secound)

      this.setState({
          interval: array
      })

  }


  clearTimer = () => {
    const {hour, minute, secound} = this.state

    this.setState({
      hour: 0,
      minute: 0,
      secound: 0,
      interval: []
    })
  }


  render() {
    return (
      <>
        <div className="container">
          <div className="row">
            <div className="col-md-8 offset-2 text-center mt-3">
              <div className="card">
                <div className="card-header">
                  <h2 className={"text-center"}>My first Timer</h2>
                </div>
                <div className="card-body">
                  <h1 className={"text-center"}>
                    {this.state.hour} : {this.state.minute} :{" "}
                    {this.state.secound}
                  </h1>
                </div>
                <div className="card-footer">
                  <div className="row">
                    <div className="col-md-3">
                      <button
                        className={"btn btn-success"}
                        onClick={this.startTimer}
                      >
                        Start
                      </button>
                    </div>
                    <div className="col-md-3">
                      <button
                        className={"btn btn-warning"}
                        onClick={this.stopTimer}
                      >
                        Stop
                      </button>
                    </div>
                    <div className="col-md-3">
                      <button
                        className={"btn btn-info"}
                        onClick={this.intervalGet}
                      >
                        Interval
                      </button>
                    </div>
                    <div className="col-md-3">
                      <button
                        className={"btn btn-danger"}
                        onClick={this.clearTimer}
                      >
                        Reset
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>



            <div>
                {
                    this.state.interval.map((item, index)=>
                        <h3 key={index}>
                            <h3 className={"text-center"}>{item}</h3>
                        </h3>
                    )
                }
            </div>



          </div>
        </div>
      </>
    );
  }
}
