import React, { Component } from "react";

export default class Table extends Component {
  state = {
    users: [
      {
        id: 1,
        name: "jilshod1",
        lastName: "Murtazoyev",
        age: 21,
      },
      {
        id: 2,
        name: "yilshod2",
        lastName: "Murtazoyev2",
        age: 21,
      },
      {
        id: 3,
        name: "ailshod3",
        lastName: "Murtazoyev3",
        age: 21,
      },
      {
        id: 4,
        name: "Dilshod4",
        lastName: "Murtazoyev4",
        age: 21,
      },
      {
        id: 5,
        name: "milshod5",
        lastName: "Murtazoyev5",
        age: 21,
      },
      {
        id: 6,
        name: "filshod6",
        lastName: "Murtazoyev6",
        age: 21,
      },
    ],
    bool: false,

    obj: {
        id: "",
        name: "",
        lastName: "",
        age: ""
    }

  };

  itemDeleted = (index) => {
    const { users } = this.state;

    users.splice(index, 1);

    this.setState({
      users,
    });
  };

  inputChanged = (event) => {
    let eventt = event.target.value;

    const { users } = this.state;

    for (let item of users) {
      let arr = item.name.split(" ");

      for (let tem of arr) {
        if (tem === eventt) {
          this.setState({
            users: [item],
          });
        }
      }
    }
  };

  addUserClicked = () => {
    const { bool } = this.state;

    this.setState({
      bool: !bool,
    });
  };


  addName = (event) => {
    const {obj} = this.state


    let name = event.target.value 

    obj.name = name

    this.setState({
        obj
    })
  }

  addLastName = (event) => {
      const {obj} = this.state

      let LastName = event.target.value

      obj.lastName = LastName

      this.setState({
          obj
      })
  }

  addAge = (event) => {
    const {obj} = this.state

    let age = event.target.value

    obj.age  = age

    this.setState({
        obj
    })
  }

 
  adeddUser = () => {
      const {users, obj, bool} = this.state

      users.push(obj)
      

      this.setState({
        users,
        bool: !bool,
        obj: {
            id: "",
            name: "",
            lastName: "",
            age: ""
        }
      })
  }


 


  render() {
    return (
      <div>
        <table border={"1px"} width={"100%"}>
          <thead>
            <tr>
              <th>Id</th>
              <th>Name</th>
              <th>Last Name</th>
              <th>Age</th>
              <th>Remove</th>
            </tr>
          </thead>
          <tbody>
            {this.state.users.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>{item.lastName}</td>
                <td>{item.age}</td>
                <td>
                  <button onClick={() => this.itemDeleted(index)}>
                    delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <br />
        <br />
        <br />

        <form action="">
          <label htmlFor="inp">Search</label>
          <br />
          <input
            type="text"
            id="inp"
            placeholder="search"
            onChange={this.inputChanged}
          />
        </form>

        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />

        {this.state.bool ? (
          <>
            <button onClick={this.addUserClicked}>close</button>

            <form action="">
              <label htmlFor="name">Name</label>
              <br />
              <input type="text" id="name" required onChange={this.addName}/> <br />
              <br />
              <br />
              <br />
              <label htmlFor="lastName">Last Name</label>
              <br />
              <input type="text" id="lastName" required onChange={this.addLastName} />
              <br />
              <br />
              <br />
              <br />
              <label htmlFor="age">age</label>
              <br />
              <input type="number" id="age" required  onChange={this.addAge}/>
              <br />
              <br />
              <br />
              <br />
              <input type="button" onClick={this.adeddUser}/>
            </form>
          </>
        ) : (
          <button onClick={this.addUserClicked}>Add user</button>
        )}
      </div>
    );
  }
}
