import React, { Component } from 'react'

export default class Radio extends Component {

    state = {
        bool: true,
        natija: '',
        inputYes: [
            {
                label: "yes",
                bool: false
            },
        ],
        imputNo: [
            {
                label: "No",
                bool: false
            }

        ]
    }

    Full = (event) => {
        const change = this.state.inputYes.bool

        let a = event.target.checked

        this.setState({
            bool: a
        })

        console.log(a);

        if (a === true) {
            let yes = this.state.inputYes.bool

            this.setState({
                bool: yes,
                natija: "yes"
            })

            if(a === fa) {
                let no = this.state.imputNo.bool

                this.setState({
                    bool: no,
                    natija: "no"
                })
            } else {
                console.log("xato no");
            }

        } else {
            console.log("xato yes");
        }
    }

        render() {

            return (
                <div>

                    <div>
                        {
                            this.state.inputYes.map((item, index) => <>
                                <label htmlFor="">{item.label}</label>
                                <input type="radio" checked={item.bool} onChange={this.Full} />
                            </>)
                        }
                    </div>

                    <div>
                        {
                            this.state.imputNo.map((item, index) => <>
                                <label htmlFor="">{item.label}</label>
                                <input type="radio" checked={item.bool} onChange={this.Full} />
                            </>)
                        }
                    </div>

                    <div>
                        <h1>{this.state.natija}</h1>
                    </div>

                </div>
            )
        }
    }
