import React, { Component } from 'react';


export default class Footer extends Component {

    state = {
        count: 0
    }

    bb = () => {
        alert('Salom Bu React Render;')
    }

    add = () => {
        let count = this.state.count;
        let b = count + 1;
        this.setState({
            count: b
        })
    }

    back = () => {
        let count = this.state.count;
        let b = count - 1;
        this.setState({
            count: b
        })
    }

    render() {

        const aa = () => {
            alert('Salom Bu React;')
        }

        return (
            <div>


                <div className='counter'>
                    <button onClick={this.back}>-</button>
                    <div>counter | {this.state.count}</div>
                    <button onClick={this.add}>+</button>
                </div>



                <button onClick={aa}>Salom</button>
                <button onClick={this.bb}>Salom</button>

            </div>
        )
    }
}
