import React, { Component } from 'react'

export default class navbar extends Component {

    state = {
        count: 0,
        countTwo: 0,
        count3: 0
    }

    minus = () => {
        let count = this.state.count
        let countMinus = count - 10

        this.setState({
            count: countMinus
        })
    }

    plus = () => {
        let count2 = this.state.count
        let countPilus = count2 + 10

        this.setState({
            count: countPilus
        })
    }

    multiply = () => {
        let count = this.state.count
        let countMultiply = count3 * 10
        this.setState({
            countTwo: countMultiply
        })
    }
    
    
    
    
    
    
    
    render() {



        return (

        <div>

            <div className='plus'>
                <button onClick={this.minus}>-</button>
                <h1>Counter | {this.state.count}</h1>
                <button onClick={this.plus}>+</button>
            </div>

            <div className="ko'paytirish">
                <button onClick={this.plus}>+</button>
                <h1>Counter | {this.state.count}</h1>
                <button onClick={this.multiply}>*</button>
            </div>

            {/* <div className="bo'lish">
                <button onClick={}>-</button>
                <h1>Counter | {this.state.count}</h1>
                <button onClick={}>+</button>
            </div> */}


        </div>

        )
    }
}
