import React, { Component } from 'react';
import "./Timer.css";

export default class Timer extends Component {

    state = {
        hour: 0,
        minute: 0,
        sekund: 0,
        interval: [],
        stopTimer: ''
    }

    moveUpHour = () => {

        let change = this.state.hour + 1

        this.setState({
            hour: change
        })

    }

    moveDawnHour = () => {
        let changeDown = this.state.hour - 1

        this.setState({
            hour: changeDown
        })
    }


    moveUpMinute = () => {

        let change = this.state.minute + 1

        this.setState({
            minute: change
        })

    }

    moveDawnMinute = () => {
        let changeDown = this.state.minute - 1

        this.setState({
            minute: changeDown
        })
    }
    moveUpSekund = () => {

        let change = this.state.sekund + 1

        this.setState({
            sekund: change
        })

    }

    moveDawnSekund = () => {
        let changeDown = this.state.sekund - 1

        this.setState({
            sekund: changeDown
        })
    }


    startTimer = () => {
        let time = setInterval(() => {
            const { hour, minute, sekund } = this.state;

            let sekundMinus = sekund - 1

            if (sekund === 0) {

                this.setState({
                    minute: minute - 1,
                    sekund: sekundMinus
                })
                if (minute === 0) {
                    this.setState({
                        hour: hour - 1,
                        // minute: 59,
                    })
                    if (hour === 0) {
                        this.setState({
                            hour: 0,
                        })
                    } else {
                        this.setState({
                            minute: 59,
                            sekund: 0
                        })
                    }
                } else {
                    this.setState({
                        sekund: 59,
                        minute: minute - 1
                    })
                }
            } else {
                this.setState({
                    sekund: sekund - 1,
                    minute: 0
                })
            }

            this.setState({
                stopTimer: time
            })


        }, 100)
    }


    render() {

        return (
            <div className="container">


                <div className="box">

                    <div className="timer">
                        <div className="item">
                            <button onClick={this.moveUpHour}>^</button>
                            <h1>{this.state.hour}</h1>
                            <button onClick={this.moveDawnHour}>V</button>
                        </div>
                        <div>
                            <h1>:</h1>
                        </div>
                        <div className="item">
                            <button onClick={this.moveUpMinute}>^</button>
                            <h1>{this.state.minute}</h1>
                            <button onClick={this.moveDawnMinute}>V</button>
                        </div>
                        <div>
                            <h1>:</h1>
                        </div>
                        <div className="item">
                            <button onClick={this.moveUpSekund}>^</button>
                            <h1>{this.state.sekund}</h1>
                            <button onClick={this.moveDawnSekund}>V</button>
                        </div>

                    </div>


                    <div className="btns">
                        <button onClick={this.startTimer}>Start</button>
                        <button onClick={this.stopFunc}>Stop</button>
                        <button onClick={this.intervalFunc}>Interval</button>
                        <button onClick={this.restartFunc}>Restart</button>

                    </div>



                </div>
            </div>
        )
    }
}
