import React, { Component } from 'react'

export default class Radio extends Component {

    state = {
        bool: true,
        natija: '',
        inputYes: [
            {
                label: "yes",
                bool: false
            },
        ],
        imputNo: [
            {
                label: "No",
                bool: false
            }

        ]
    }

    Full = (event) => {
        const change = this.state.inputYes.bool

        let a = event.target.checked

        this.setState({
            bool: a
        })

        console.log(a);

        if (a === true) {
            

            this.setState({
                bool: true,
                natija: "yes"
            })


        } else {
            console.log("xato yes");
        }
    }

    Fill = (event) => {
        const change = this.state.imputNo.bool

        let b = event.target.checked

        this.setState({
            bool: b
        })


        if(b === true) {
            this.setState({
                bool
            })
        }
    }

    render() {

        return (
            <div>

                <div>
                    {
                        this.state.inputYes.map((item, index) => <>
                            <label htmlFor="">{item.label}</label>
                            <input type="radio" checked={item.bool} onChange={this.Full} />
                        </>)
                    }
                </div>

                <div>
                    {
                        this.state.imputNo.map((item, index) => <>
                            <label htmlFor="">{item.label}</label>
                            <input type="radio" checked={item.bool} onChange={this.Fill} />
                        </>)
                    }
                </div>

                <div>
                    <h1>{this.state.natija}</h1>
                </div>

            </div>
        )
    }
}
